{"changed":true,"filter":false,"title":"sitters_controller.rb","tooltip":"/app/controllers/sitters_controller.rb","value":"class SittersController < ApplicationController\n  before_action :set_sitter, only: [:show, :edit, :update, :destroy]\n\n  # GET /sitters\n  # GET /sitters.json\n  def index\n    @sitters = Sitter.all\n    @user = current_user\n  end\n\n  # GET /sitters/1\n  # GET /sitters/1.json\n  def show\n  end\n\n  # GET /sitters/new\n  def new\n    @sitter = Sitter.new\n  end\n\n  # GET /sitters/1/edit\n  def edit\n  end\n\n  # POST /sitters\n  # POST /sitters.json\n  def create\n    @user = current_user.id\n    @sitter = Sitter.create(sitter_params)\n    @sitter.update(user_id: @user)\n   \n\n    respond_to do |format|\n      if @sitter.save\n        format.html { redirect_to @sitter, notice: 'Sitter was successfully created.' }\n        format.json { render :show, status: :created, location: @sitter }\n      else\n        format.html { render :new }\n        format.json { render json: @sitter.errors, status: :unprocessable_entity }\n      end\n    end\n  end\n\n  # PATCH/PUT /sitters/1\n  # PATCH/PUT /sitters/1.json\n  def update\n    respond_to do |format|\n      if @sitter.update(sitter_params)\n        format.html { redirect_to @sitter, notice: 'Sitter was successfully updated.' }\n        format.json { render :show, status: :ok, location: @sitter }\n      else\n        format.html { render :edit }\n        format.json { render json: @sitter.errors, status: :unprocessable_entity }\n      end\n    end\n  end\n\n  # DELETE /sitters/1\n  # DELETE /sitters/1.json\n  def destroy\n    @sitter.destroy\n    respond_to do |format|\n      format.html { redirect_to sitters_url, notice: 'Sitter was successfully destroyed.' }\n      format.json { head :no_content }\n    end\n  end\n\n  private\n    # Use callbacks to share common setup or constraints between actions.\n    def set_sitter\n      @sitter = Sitter.find(params[:id])\n    end\n\n    # Never trust parameters from the scary internet, only allow the white list through.\n    def sitter_params\n      params.require(:sitter).permit(:dog_id, :summary, :years_of_experience, :insurance, :full_time, :doggie_cam, :solo_care, :avatar)\n      params.require(:sitter).permit(:dog_id, :fname, :lname, :summary, :years_of_experience, :insurance, :full_time, :doggie_cam, :solo_care)\n    end\nend","undoManager":{"mark":25,"position":29,"stack":[[{"group":"doc","deltas":[{"action":"removeText","range":{"start":{"row":75,"column":0},"end":{"row":75,"column":12}},"text":"<<<<<<< HEAD"},{"action":"insertText","range":{"start":{"row":75,"column":0},"end":{"row":75,"column":1}},"text":"="}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":75,"column":1},"end":{"row":75,"column":2}},"text":"="}]}],[{"group":"doc","deltas":[{"action":"removeText","range":{"start":{"row":75,"column":1},"end":{"row":75,"column":2}},"text":"="}]}],[{"group":"doc","deltas":[{"action":"removeText","range":{"start":{"row":75,"column":0},"end":{"row":75,"column":1}},"text":"="}]}],[{"group":"doc","deltas":[{"action":"removeText","range":{"start":{"row":74,"column":21},"end":{"row":75,"column":0}},"text":"\n"}]}],[{"group":"doc","deltas":[{"action":"removeText","range":{"start":{"row":76,"column":0},"end":{"row":76,"column":7}},"text":"======="}]}],[{"group":"doc","deltas":[{"action":"removeText","range":{"start":{"row":75,"column":135},"end":{"row":76,"column":0}},"text":"\n"}]}],[{"group":"doc","deltas":[{"action":"removeText","range":{"start":{"row":77,"column":0},"end":{"row":77,"column":48}},"text":">>>>>>> 2cd81d96ab032599c01eab39fa1a6bf72a68f52c"}]}],[{"group":"doc","deltas":[{"action":"removeText","range":{"start":{"row":76,"column":142},"end":{"row":77,"column":0}},"text":"\n"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":76,"column":142},"end":{"row":76,"column":143}},"text":"f"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{